var alists = require('../alists');
var assert = require('assert');

exports.toObj = function() {
    assert.deepEqual(alists.toObj([['a', 1], ['b', 2]]),
               {'a': 1, 'b': 2});
}

exports.toList = function() {
    assert.eql(alists.toList({'a': 1, 'b': 2}),
               [['a', 1], ['b', 2]]);
}
